import React from 'react'
import './App.css';

class App extends React.Component {
state = {
  email: '',
  saveEmail: '',
}

capturaEmail = (event) => {
  this.setState({
    email: event.target.value,
  })
}

enviar = () => {
  const { email } = this.state;
  this.setState({
    saveEmail: email,
  })
}

  render() {

    const {saveEmail } = this.state
    return (
      <div className="App">
        <label htmlFor="email">Email</label>
        <input
        type="email"
        id='email'
        type='email'
        onChange={this.capturaEmail}/>
        <input
        type='button'
        value='Enviar'
        data-testid='id-send'
        onClick={this.enviar}/>
        <input type='button' value='Voltar' onClick={this.voltar} />

        <h4>Email: { saveEmail }</h4>
      </div>
    );
  }

}

export default App;
